(70, 1, '2015-01-23 10:47:32', '2015-01-23 12:47:32', '<span style=\"color: #000000;\">/********************************************************/</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">/* Mosta o SQL que cada sessão está executando */</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">/********************************************************/</span><br style=\"color: #cccccc;\" /><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">go</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">SELECT [Spid] = session_Id</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">, ecid</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">, [Database] = DB_NAME(sp.dbid)</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">, [User] = nt_username</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">, [Status] = er.status</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">, [Wait] = wait_type</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">, [Individual Query] = SUBSTRING (qt.text,</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">er.statement_start_offset/2,</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">(CASE WHEN er.statement_end_offset = -1</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">THEN LEN(CONVERT(NVARCHAR(MAX), qt.text)) * 2</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">ELSE er.statement_end_offset END -</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">er.statement_start_offset)/2)</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">,[Parent Query] = qt.text</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">, Program = program_name</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">, Hostname</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">, nt_domain</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">, start_time</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">FROM sys.dm_exec_requests er</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">INNER JOIN sys.sysprocesses sp ON er.session_id = sp.spid</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">CROSS APPLY sys.dm_exec_sql_text(er.sql_handle)as qt</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">WHERE session_Id &gt; 50 -- Ignore system spids.</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">AND session_Id NOT IN (@@SPID) -- Ignore this current statement.</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">ORDER BY 1, 2</span><br style=\"color: #cccccc;\" /><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">SELECT SDER.[statement_start_offset],</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">SDER.[statement_end_offset],</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">CASE</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">WHEN SDER.[statement_start_offset] &gt; 0 THEN</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">--The start of the active command is not at the beginning of the full command text</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">CASE SDER.[statement_end_offset]</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">WHEN -1 THEN</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">--The end of the full command is also the end of the active statement</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">SUBSTRING(DEST.TEXT, (SDER.[statement_start_offset]/2) + 1, 2147483647)</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">ELSE</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">--The end of the active statement is not at the end of the full command</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">SUBSTRING(DEST.TEXT, (SDER.[statement_start_offset]/2) + 1, (SDER.[statement_end_offset] - SDER.[statement_start_offset])/2)</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">END</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">ELSE</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">--1st part of full command is running</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">CASE SDER.[statement_end_offset]</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">WHEN -1 THEN</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">--The end of the full command is also the end of the active statement</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">RTRIM(LTRIM(DEST.[text]))</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">ELSE</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">--The end of the active statement is not at the end of the full command</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">LEFT(DEST.TEXT, (SDER.[statement_end_offset]/2) +1)</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">END</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">END AS [executing statement],</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">DEST.[text] AS [full statement code]</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">FROM sys.[dm_exec_requests] SDER CROSS APPLY sys.[dm_exec_sql_text](SDER.[sql_handle]) DEST</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">WHERE SDER.session_id &gt; 50</span><br style=\"color: #cccccc;\" /><span style=\"color: #000000;\">ORDER BY SDER.[session_id], SDER.[request_id]</span>\r\n\r\n&nbsp;\r\n\r\nCopiado de : http://dba-sqlserver.blogspot.com.br/2009/08/o-que-o-seu-sqlserver-esta-executando.html\r\n\r\n&nbsp;', 'SQL SERVER - Querys sendo executadas no banco', '', 'publish', 'open', 'open', '', 'querys-sendo-executadas-no-banco', '', '', '2015-01-23 10:47:58', '2015-01-23 12:47:58', '', 0, 'http://www.geovanigeraldi.com.br/?p=70', 0, 'post', '', 0),
